import React from 'react';
import {GoogleApiWrapper, Marker, InfoWindow} from 'google-maps-react';
import CurrentLocation from './CurrentLocation';
import CurrentPerson from './CurrentPerson';

class MapView extends React.Component {
  state = {
    showingInfoWindow: {},
    activeMarker: {},
    selectedPlace: {}
  }
  static defaultProps = {
    zoom: 14
  }

  onMarkerClick =(props, marker, e)=>{
    this.setState({
      selectedPlace:props,
      activeMarker: marker,
      showingInfoWindow: true
    })
  }

  onClose = (props)=>{
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker:null
      })
    }
  }


  render(){
    let parkingSpots= this.props.parkingSpots.map(spot=> <Marker name={'Kenyatta International Convention Centre'} onClick={this.onMarkerClick}position={{lat:spot.lat, lng:spot.lng}}/>)
    return(
        <CurrentLocation
        center = {this.props.center}
        grabRecenter={this.props.grabRecenter}
          centerAroundCurrentLocation
          google={this.props.google}
        >

          <CurrentPerson center={this.props.center} radius={this.props}name={'You Are Here'} onClick={this.onMarkerClick} position ={this.props.center}/>
          {parkingSpots}
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
        </CurrentLocation>
    )
  }
}

export default GoogleApiWrapper({apiKey: "AIzaSyDS7fqSLY5L2xuXAb8cIb1vyQRKt5EPXmw"}) (MapView)
