import React from 'react';
import { Map, GoogleApiWrapper, Marker, InfoWindow} from 'google-maps-react';
import CurrentLocation from './CurrentLocation';
import MapControl from './MapControl';
const mapStyles = {
  width: '100%',
  height: '100%'
};


class MapView extends React.Component {
  state = {
    showingInfoWindow: {},
    activeMarker: {},
    selectedPlace: {},
    recenterMap: null
  }
  static defaultProps = {
    zoom: 14
  }

  onMarkerClick =(props, marker, e)=>{
    this.setState({
      selectedPlace:props,
      activeMarker: marker,
      showingInfoWindow: true
    })
  }

  onClose = (props)=>{
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker:null
      })
    }
  }

  grabRecenter = (func)=>{
    this.setState({
      recenterMap: func
    })
  }



  render(){
    let parkingSpots= this.props.parkingSpots.map(spot=> <Marker name={'Kenyatta International Convention Centre'} onClick={this.onMarkerClick}position={{lat:spot.lat, lng:spot.lng}}/>)
    return(
        <CurrentLocation
        grabRecenter={this.grabRecenter}
          centerAroundCurrentLocation
          google={this.props.google}
        >
        <button className="center-map">Center Map</button>
          <Marker name={'Kenyatta International Convention Centre'} onClick={this.onMarkerClick} position ={{lat:40.7505, lng:73.9934}}/>
          {parkingSpots}
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
        </CurrentLocation>
    )
  }
}

export default GoogleApiWrapper({apiKey: "AIzaSyDS7fqSLY5L2xuXAb8cIb1vyQRKt5EPXmw"}) (MapView)
