import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Geocode from "react-geocode";
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

Geocode.setApiKey("AIzaSyDS7fqSLY5L2xuXAb8cIb1vyQRKt5EPXmw");

const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
}));

export default function ParkingForm(props) {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    address: '',
    spotsNum:''
  });

  const handleChange = address => event => {
    setValues({ ...values,
      [address]: event.target.value
    });
  };

  const handleSubmit = (address, spotsNum)=>{
    console.log("spotsNum",spotsNum)
    let addressObject = {address: address, spotsNum:spotsNum, fromRentor: true}
    props.socket.emit('client:message', addressObject)

  }

  const handleSpotsNumber = event =>{
    setValues({ ...values,
      [values.spotsN]: event.target.value
    });
  }

  let getCurrentLocation = ()=>{
    navigator.geolocation.getCurrentPosition(position=> console.log(position))
  }



  return (
    <Card className = {classes.card}>
      <CardActionArea >
        <CardMedia className = {classes.media} image = "../assets/parking_form_pic.jpeg" title = "Contemplative Reptile" />
          <CardContent >
            <form>
            {console.log(props.socket)}
              <TextField name="addess"id = "outlined-name"label = "Address"className = {classes.textField}
              value = {values.address} onChange = {handleChange('address')} margin = "normal"variant = "outlined" />
              <Select
                value={values.spotsNum}
                onChange={handleSpotsNumber}
                inputProps={{
                name: 'spotsNum',
                id: 'age-simple',
                  }}
                  >
                  <MenuItem value={1}>1</MenuItem>
                  <MenuItem value={2}>2</MenuItem>
                  <MenuItem value={3}>3</MenuItem>
                  <MenuItem value={4}>4</MenuItem>
                  <MenuItem value={5}>5</MenuItem>
                  <MenuItem value={6}>6</MenuItem>
                  <MenuItem value={7}>7</MenuItem>
                  <MenuItem value={8}>8</MenuItem>
                  <MenuItem value={9}>9</MenuItem>
                  <MenuItem value={10}>10</MenuItem>
              </Select>
            </form>
          </CardContent>
      </CardActionArea>
      <CardActions >
        <Button onClick={()=>handleSubmit(values.address, values.spotsNum)}size = "small" color = "primary" >Find Parking!</Button>
        <Button onClick={()=>getCurrentLocation()}size = "small" color = "primary" >Get Current Position!</Button>
      </CardActions>
    </Card>
  );
}
