import React from 'react';
import Home from './Home'
import './App.css';
import NavBar from './components/NavBar'
import SignUp from './components/SignUp'
import Profile from './components/Profile'
import MapView from './components/MapView'
import ParkingForm from './components/ParkingForm'
import {Switch, Route, Redirect} from 'react-router-dom'

class App extends React.Component {
  state = {
    user: null,
    parkingSpots: [],
    currentLocation:{lat:40.73,lng:-73.93}
  }

  componentDidMount() {
      navigator.geolocation.getCurrentPosition(position=> this.setState({lat:position.coords.latitude, lng:position.coords.longitude}))
  }

  getParkingSpot = (spot) =>{
    console.log("Parking Spot", spot)
    this.setState({
      parkingSpots:[...this.state.parkingSpots, spot]})
  }

  logOut = () => {
    if (localStorage.getItem('token')) {
      localStorage.removeItem('token')
      this.setState({user: null})
    } else {
      return null
    }
  }

  createNewUser = (user) => {
    fetch('http://localhost:3000/api/v1/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user})
    }).then(res => res.json()).then(res => {
      localStorage.setItem("token", res.jwt)
      this.setState({user: res.user})
      this.getProfile(res.jwt)
    }).then(res => <Redirect to="/"/>).catch(err => console.log(err))
  }

  logIn = (user) => {
    fetch('http://localhost:3000/api/v1/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user})
    }).then(res => res.json()).then(res => {
      localStorage.setItem("token", res.jwt)
      this.setState({user: res.user})
      this.getProfile(res.jwt)
    }).catch(err => console.log(err))

  }

  getProfile = (token) => {
    fetch('http://localhost:3000/api/v1/profile', {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${token}`
      }
    }).then(res => res.json()).then(res => this.setState({user: res.user}))
  }

  render() {
    console.log("places",this.state.currentLocation)
    return (<div className="app-container">
      <NavBar user={this.state.user} logOut={this.logOut}/>
      <Switch>
        <Route exact="exact" path='/' component={Home}/>
        <Route exact="exact" path='/parkingform' render={() => <ParkingForm getParkingSpot={this.getParkingSpot}/>}/>
        <Route exact="exact" path='/map' render={() => <MapView parkingSpots={this.state.parkingSpots} />}/>
        <Route exact="exact" path='/signup' render={() =>< SignUp createNewUser = {
            this.createNewUser
          } />}/>
        <Route exact="exact" path='/profile' render={() =>< Profile user = {
            this.state.user
          } />}/>
      </Switch>

    </div>);
  }

}

export default App;
