import React from 'react';
import './App.css';
import NavBar from './components/NavBar'
import Login from './components/Login'
import SignUp from './components/SignUp'
import Profile from './components/Profile'
import {Switch, Route} from 'react-router-dom'

class App extends React.Component{
  state = {
    user: null,
    token: '',
    auth: true,
  }

  componentDidMount(){
    let token = localStorage.getItem('token')
    fetch("http://localhost:3000/api/v1/current_user",{
      method: 'GET',
      headers:{
        'Content-Type': "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${token}`
      }
    })
    .then(res=> res.json())
    .then(res => this.setState({
      user:res.user
    }))
  }

logOut = ()=>{
if (localStorage.getItem('token')) {
  localStorage.removeItem('token')
  this.setState({user:null})
}else {
  return null
}
}

createNewUser = (user)=>{
  fetch('http://localhost:3000/api/v1/users',{
    method: 'POST',
    headers: {
      'Content-Type':'application/json',
      Accept:'application/json'
    },
    body: JSON.stringify({user})
  })
  .then(res =>res.json())
  .then(res =>{
    localStorage.setItem("token", res.jwt)
    this.setState({
      user:res.user
    })
    this.getProfile(res.jwt)
  })
  .catch(err=>console.log(err))
}

logIn = (user)=>{
  fetch('http://localhost:3000/api/v1/login',{
    method: 'POST',
    headers: {
      'Content-Type':'application/json',
      Accept:'application/json'
    },
    body: JSON.stringify({user})
  })
  .then(res =>res.json())
  .then(res =>{ console.log("In logIn submit", res)
    localStorage.setItem("token", res.jwt)
    this.setState({
      user:res.user
    })
    this.getProfile(res.jwt)
  })
  .catch(err=>console.log(err))

}

getProfile = (token)=>{
  fetch('http://localhost:3000/api/v1/profile', {
  method: 'GET',
  headers: {
    Authorization: `Bearer ${token}`
  }
})
.then(res=> res.json())
.then(res => console.log('In get Profile', res))
}

  render(){
    console.log("In render", this.state.user)
    return (
      <div className="app-container">
        <NavBar user={this.state.user}/>
        <Switch>
          <Route exact path='/'/>
          <Route exact path='/login' render={()=> <Login logIn={this.logIn}/> }/>
          <Route exact path='/signup' render={()=><SignUp createNewUser={this.createNewUser}/>}/>
          <Route exact path='/profile' render={()=><Profile/>}/>
        </Switch>
      </div>
    );
  }

}


export default App;
