import io from 'socket.io-client';
import config from './config';

Geocode.setApiKey("AIzaSyDS7fqSLY5L2xuXAb8cIb1vyQRKt5EPXmw");

class Home extends React.Component{
  socket = {};

  constructor(){
    super()
    this.state = {
      addresses:[],
    }
    this.socket = io(config.api).connect()
  }

componentDidMount(){
  this.socket.on('server:message', location => {
    this.addAddress(location)
  })
}
  addAddress =(address)=>{
      this.setState({ addresses:[...this.state.addresses,address] })
    }

  findParking = (input)=>{
    Geocode.fromAddress(input).then(
  response => {
    const locationObject = response.results[0].geometry.location;
    console.log("locationObject",locationObject);
    this.addAddress(locationObject)
    locationObject.fromRentee = true;
    this.socket.emit('client:message', locationObject)
    Popup.create({
    title: null,
    content: 'This popup uses the create method directly to get more control. This popup demonstrates custom buttons.',
    buttons: {
        left: [{
            text: 'Cancel',
            className: 'danger',
            action: function () {
                Popup.alert('You pressed the Cancel btn');

                /** Close this popup. Close will always close the current visible one, if one is visible */
                Popup.close();
            }
        }],
        right: [{
            text: 'Alt',
            key: 'ctrl+enter',
            action: function () {
                // Passing true as the second argument to the create method
                // displays it directly, without interupting the queue.
                Popup.create({
                    title: null,
                    content: 'I was configured to display right away, without affecting the queue. Closing this will display the previously visible popup.',
                    buttons: {
                        left: ['cancel'],
                        right: []
                    }
                }, true);
            }
        }, {
            text: 'Save',
            className: 'success',
            action: function () {
                Popup.alert('You pressed the Save btn');

                /** Close this popup. Close will always close the current visible one, if one is visible */
                Popup.close();
            }
        }]
    }
});
  },
  error => {
    console.error(error);
  }
);
  }
render(){
  console.log(this.state.addresses);
  return (
      <div className="home-container">
      <header className='header'>
            <div className="header__logo-box">
            </div>
            <div className="header__text-box">
              <h1 className="heading-primary">
                <span className="heading-primary--main">Open Spaces</span>
                <span className="heading-primary--sub">Find Parking</span>
              </h1>
              <SearchBar findParking={this.findParking}/>
            </div>
            <br/>
            </header>
      </div>
    );
  }

}
