import React from 'react';
import {
  makeStyles
} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Geocode from "react-geocode";

Geocode.setApiKey("AIzaSyDS7fqSLY5L2xuXAb8cIb1vyQRKt5EPXmw");

const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
}));

export default function ParkingForm(props) {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    address: '',
  });

  const handleChange = address => event => {
    setValues({ ...values,
      [address]: event.target.value
    });
    Geocode.fromAddress(address).then(
      response => {
        const {
          lat,
          lng
        } = response.results[0].geometry.location;
       props.getParkingSpot({lat, lng});
      },
      error => {
        console.error(error);
      }
    );
  };



  return ( <
    Card className = {
      classes.card
    } >
    <
    CardActionArea >
    <
    CardMedia className = {
      classes.media
    }
    image = "../assets/parking_form_pic.jpeg"
    title = "Contemplative Reptile" /
    >
    <
    CardContent >
    <
    form >
    <
    TextField id = "outlined-name"
    label = "Address"
    className = {
      classes.textField
    }
    value = {
      values.address
    }
    onChange = {
      handleChange('address')
    }
    margin = "normal"
    variant = "outlined" /
    >
    <
    /form> <
    /CardContent> <
    /CardActionArea> <
    CardActions >
    <
    Button size = "small"
    color = "primary" >
    Share <
    /Button> <
    Button size = "small"
    color = "primary" >
    Learn More <
    /Button> <
    /CardActions> <
    /Card>
  );
}
