import React from 'react';
import Home from './Home'
import './App.css';
import NavBar from './components/NavBar'
import SignUp from './components/SignUp'
import Profile from './components/Profile'
import MapView from './components/MapView'
import ParkingForm from './components/ParkingForm'
import {Switch, Route, Redirect, withRouter} from 'react-router-dom'
import Popup from 'react-popup';
import HttpsRedirect from 'react-https-redirect';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      user: null,
      parkingSpots: [],
      currentLocation:{lat:40.73,lng:-73.93},
      isRentor: false,
      socket: {}
    }
  }


  componentDidMount(){
      navigator.geolocation.getCurrentPosition(position=> this.setState({lat:position.coords.latitude,lng:position.coords.longitude}))
  }

  getParkingSpot = (spot) =>{
    console.log("Parking Spot", spot)
    this.setState({
      parkingSpots:[...this.state.parkingSpots, spot]})
  }

  getSocket = (socket)=>{
    console.log("In App",socket);
    this.setState({socket: socket})
  }

  logOut = () => {
    if (localStorage.getItem('token')) {
      localStorage.removeItem('token')
      this.setState({user: null})
    } else {
      return null
    }
  }

  createNewUser = (user) => {
    fetch('https://open-spaces-api.herokuapp.com/api/v1/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user})
    }).then(res => res.json()).then(res => {
      localStorage.setItem("token", res.jwt)
      this.setState({user: res.user})
      this.getProfile(res.jwt)
    }).then(res => <Redirect to="/"/>).catch(err => console.log(err))
  }

  logIn = (user) => {
    fetch('https://open-spaces-api.herokuapp.com/api/v1/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user})
    }).then(res => res.json()).then(res => {
      localStorage.setItem("token", res.jwt)
      this.setState({user: res.user})
      this.getProfile(res.jwt)
    }).catch(err => console.log(err))

  }

  getProfile = (token) => {
    fetch('https://open-spaces-api.herokuapp.com/api/v1/profile', {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${token}`
      }
    }).then(res => res.json()).then(res => this.setState({user: res.user}))
  }

  render() {
    console.log(this.state.socket);
    return (<div className="app-container">
    <HttpsRedirect>
      <NavBar user={this.state.user} logOut={this.logOut}/>
      <Popup />
      <Switch>
        <Route exact="exact" path='/' render={() => <Home getParkingSpot={this.getParkingSpot} isRentor={this.state.isRentor} getSocket={this.getSocket}/>}/>
        <Route exact="exact" path='/parkingform' render={() => <ParkingForm getParkingSpot={this.getParkingSpot} socket={this.state.socket}/>}/>
        <Route exact="exact" path='/map' render={() => <MapView parkingSpots={this.state.parkingSpots} />}/>
        <Route exact="exact" path='/signup' render={() =>< SignUp createNewUser = {
            this.createNewUser
          } />}/>
        <Route exact="exact" path='/profile' render={() =>< Profile user = {
            this.state.user
          } />}/>
      </Switch>
      </HttpsRedirect>
    </div>);
  }

}

export default withRouter(App);
